import { jsx as _jsx } from "react/jsx-runtime";
import classNames from 'classnames';
import { useTheme } from '../Flowbite/ThemeContext';
export const HelperText = ({ value, children, color = 'default', className, ...props }) => {
    const theme = useTheme().theme.helperText;
    return (_jsx("p", { className: classNames(theme.base, theme.colors[color], className), ...props, children: value ?? children ?? '' }));
};
