import { jsx as _jsx } from "react/jsx-runtime";
import classNames from 'classnames';
import { useTheme } from '../Flowbite/ThemeContext';
export const Label = ({ children, color = 'default', disabled = false, value, className, ...props }) => {
    const theme = useTheme().theme.label;
    return (_jsx("label", { className: classNames(theme.base, theme.colors[color], disabled ?? theme.disabled, className), ...props, children: value ?? children ?? '' }));
